import React, { useState } from 'react';
import Header from './Header';
import './App.css';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import FaceRecog from "./FaceRecog";
import Checkout from './Checkout';
import ProductSelect from './ProductSelect';
const url = "http://127.0.0.1:8000";


function App() {
  const [FaceDetected,setFaceDetected] = useState([]);
  setTimeout(() => {
     fetch(url+"/userdata")
           .then((response) => {
            //  console.log(response.json());
             return response.json();
            })
            .then((data)=>{
              setFaceDetected(data);
              console.log(data);
            })},10000);

      // const requestOptions = {
      //     method: 'POST',
      //     headers: { 'Content-Type': 'application/json' },
      //     body: JSON.stringify([{"user_id":"614ffa63-0c67-11eb-bdcf-03b990a51dac","product_id":"5a5ea613-40a1-41b1-92f3-00fa4eb251d8","product_quantity":2},{"user_id":"614ffa63-0c67-11eb-bdcf-03b990a51dac","product_id":"5899b6bd-0b9e-48b9-be4b-4ccbf75188ab","product_quantity":1}])
      // };
      // fetch(url+'/placeorder', requestOptions)
      //     .then(response => response.json())
      //     .then(data => {return { postId: data.id }});

      return (
        <Router>
        <div className="App">

          <Switch>
              <Route path="/checkout">
                <Header />
                <Checkout />
              </Route>
              <Route path="/productselect">
                <Header />
                <ProductSelect />
              </Route>
              <Route path="/">
                <Header />
                <div className="front">
                  <div>
                    <img src={url+"/video"}></img>
                  </div>
                  <div className="App__container">
                    <div className="App__list">
                      {
                        FaceDetected.map((item,i)=> 
                        <FaceRecog key={i} name={item.user_name} address={item.user_address} path={url+item.user_image} phone={item.user_phone}/>

                        )}
                        </div>
                    </div>
                  </div>

              </Route>
            </Switch>
          </div>

      </Router>
      );
    }

    export default App;
